function togglePosterInfo(){$(".poster__info-open").each(function(){$(this).on("click",function(o){var e=$($(this).attr("href"));e.find(".poster__info").addClass("is-open").removeClass("is-closed"),o.preventDefault()})}),$(".poster__info-close").each(function(){$(this).on("click",function(o){var e=$($(this).attr("href"));e.find(".poster__info").addClass("is-closed").removeClass("is-open"),o.preventDefault()})})}function initPage(){console.log("page loaded"),togglePosterInfo()}